document.addEventListener("DOMContentLoaded",()=>{const e=document.querySelector(".slide-track");if(e&&e.children.length>1){const s=Array.from(e.children);let n=0;s.forEach(t=>{const n=t.cloneNode(!0);e.appendChild(n)});function t(){n++,e.style.transition="transform 0.5s ease-in-out",e.style.transform=`translateX(-${n*100}%)`,n>=s.length&&setTimeout(()=>{e.style.transition="none",n=0,e.style.transform=`translateX(0)`},500)}setInterval(t,6e3)}})